import numpy as npfrom scipy.io import wavfileimport matplotlib.pyplot as pltimport sounddevice as sd# --- Load WAV ---sample_rate, dataF = wavfile.read("aero_car_noise.wav")def plotFFT(data):    # If stereo, convert to mono    if data.ndim > 1:        data = data.mean(axis=1)        # --- FFT ---    N = len(data)    fft_vals = np.fft.fft(data)    fft_freqs = np.fft.fftfreq(N, 1/sample_rate)        # Keep only positive freq half    pos_mask = fft_freqs >= 0    fft_vals = fft_vals[pos_mask]    fft_freqs = fft_freqs[pos_mask]        # --- Plot ---    plt.plot(fft_freqs, np.abs(fft_vals) / np.max(np.abs(fft_vals)))    plt.xlabel("Frequency (Hz)")    plt.ylabel("Magnitude")    plt.xlim(0,  800)    plt.tight_layout()    plt.show()        k = 10    largest = np.partition(np.abs(fft_vals), -k)[-k:]        return largest#sd.play(data, sample_rate)def make_sine_sum(freqs, mags, fs, t):    """    freqs : list of frequencies in Hz    fs    : sampling rate in Hz    returns: function f(t) that evaluates the sum of sin waves    """    freqs = np.array(freqs)    f = 0    for i, j in zip(freqs, mags):         signal = j * np.sin(2 * np.pi * i * t)        f+= signal                return f# --- Example usage ---fs = 44100  # sampling rateduration = 1  # seconds# Time array for 2 secondst = np.linspace(0, duration, int(fs * duration), endpoint=False)# Make a sine-sum function for some frequenciesfl = [175, 250, 380, 410, 320]ml = [1, 2, 1.2, 1.2]signal = make_sine_sum(fl, ml, fs, t)sd.play(signal, fs)t10 = plotFFT(dataF)#plotFFT(fftData)plotFFT(signal)